import s from "./Dialogs.module.css"
import { NavLink } from "react-router-dom"
import DialogItem from "./DialogItem.js/DialogItex"
import Message from "./Message/Meesage"
import {
  sendMessageCreator,
  updateNewMessageBodyCreator,
} from "../../redux/state"

const Dialogs = (props) => {
  let state = props.store.getState().messagesPage
  let dialogsElements = state.dials.map((elem) => (
    <DialogItem name={elem.name} id={elem.id} />
  ))
  let messageElements = state.messages.map((elem) => (
    <Message message={elem.message} />
  ))
  let newMessageBody = state.newMessageBody

  let onSendMessageClick = () => {
    props.dispatch(sendMessageCreator())
  }
  let onNewMessageChange = (e) => {
    let body = e.target.value
    props.store.dispatch(updateNewMessageBodyCreator(body))
  }

  return (
    <div className={s.dialogs}>
      <div className={s.dialogItems}>{dialogsElements}</div>
      <div className={s.messages}>
        <div>{messageElements}</div>
        <div>
          <textarea
            placeholder="Enter Your Message"
            onChange={onNewMessageChange}
            value={newMessageBody}
          ></textarea>
        </div>
        <div>
          <button onClick={onSendMessageClick}>Send</button>
        </div>
      </div>
    </div>
  )
}

export default Dialogs
