import React from "react"
import s from "./Users.module.css"

let Users = (props) => {
  if (props.users.length === 0) {
    axios.get("https://social-network.samuraijs.com/api/1.0/users")
    props.setUsers([
      {
        name: "Odel",
        id: 1,
        location: { city: "Raifa", country: "Russia" },
        status: "Darova",
        followed: true,
        photoUrl: "https://avatarzo.ru/wp-content/uploads/squid-game-anime.jpg",
      },
      {
        name: "Vodim",
        id: 2,
        location: { city: "Legend", country: "Russ" },
        status: "Yooo",
        followed: true,
        photoUrl: "https://avatarzo.ru/wp-content/uploads/squid-game-anime.jpg",
      },
      {
        name: "Ortem",
        id: 3,
        location: { city: "Dela", country: "USSR" },
        status: "U menya dela",
        followed: false,
        photoUrl: "https://avatarzo.ru/wp-content/uploads/squid-game-anime.jpg",
      },
      {
        name: "Demba",
        id: 4,
        location: { city: "Aznakay", country: "RF" },
        status: "Timur Badakshin",
        followed: false,
        photoUrl: "https://avatarzo.ru/wp-content/uploads/squid-game-anime.jpg",
      },
    ])
  }
  return (
    <div>
      {props.users.map((u) => (
        <div key={u.id}>
          <span>
            <div>
              <img src={u.photoUrl} className={s.userPhoto} />
            </div>
            <div>
              {u.followed ? (
                <button
                  onClick={() => {
                    props.unfollow(u.id)
                  }}
                >
                  Unfollow
                </button>
              ) : (
                <button
                  onClick={() => {
                    props.follow(u.id)
                  }}
                >
                  Follow
                </button>
              )}
            </div>
          </span>
          <span>
            <span>
              <div>{u.name}</div>
              <div>{u.status}</div>
            </span>
            <span>
              <div>{u.location.country}</div>
              <div>{u.location.city} </div>
            </span>
          </span>
        </div>
      ))}
    </div>
  )
}

export default Users
