const FOLLOW = "FOLLOW"
const UNFOLLOW = "UNFOLLOW"
const USERS = "USERS"

let initialState = {
  users: [
    {
      name: "Odel",
      id: 1,
      location: { city: "Raifa", country: "Russia" },
      status: "Darova",
      followed: true,
    },
    {
      name: "Vodim",
      id: 2,
      location: { city: "Legend", country: "Russ" },
      status: "Yooo",
      followed: true,
    },
    {
      name: "Ortem",
      id: 3,
      location: { city: "Dela", country: "USSR" },
      status: "U menya dela",
      followed: false,
    },
    {
      name: "Demba",
      id: 4,
      location: { city: "Aznakay", country: "RF" },
      status: "Timur Badakshin",
      followed: false,
    },
  ],
}

const usersReducer = (state = initialState, action) => {
  switch (action.type) {
    case FOLLOW:
      return {
        ...state,
        users: state.users.map((u) => {
          if (u.id === action.id) {
            return { ...u, followed: true }
          }
          return u
        }),
      }

    case UNFOLLOW:
      return {
        ...state,
        users: state.users.map((u) => {
          if (u.id === action.id) {
            return { ...u, followed: false }
          }
          return u
        }),
      }
    case USERS:
      return {
        ...state,
        users: [...state.users, ...action.users],
      }
    default:
      return state
  }
}
export const followAC = (userId) => {
  return {
    type: FOLLOW,
    userId,
  }
}
export const unFollowAC = (userId) => {
  return {
    type: UNFOLLOW,
    userId,
  }
}

export const setUsersAC = (users) => {
  return {
    type: USERS,
    users,
  }
}

export default usersReducer
